#!/bin/bash
APP_NAME="FlappyBok.app" && INSTALL_VERSION="${1:-1.0.1-arm64}" && INSTALL_URL="http://github.com/ChezCoder/Distributions/blob/main/MacOS/FlappyBok/v$INSTALL_VERSION.zip?raw=true" && builtin echo -e "\033[32mPreparing to install FlappyBok v$INSTALL_VERSION \033[0m" && builtin echo -e "\033[32mDownloading files...\033[0m" && cd && curl -L $INSTALL_URL --output payload && builtin echo -e "\033[32mExpanding...\033[0m" && unzip -q -o payload && builtin echo -e "\033[32mInstalling...\033[0m" && xattr -d -r com.apple.quarantine "v$INSTALL_VERSION/$APP_NAME" && rm -r -f "~/Desktop/$APP_NAME" && mv "v$INSTALL_VERSION/$APP_NAME" ~/Desktop/$APP_NAME && rm -f -r "__MACOSX" && rm -f -r "v$INSTALL_VERSION" && rm -r -f "payload" && builtin echo -e "\033[32mInstalled to Desktop!\033[0m" && exit 0; builtin echo -e "\033[31mSomething went wrong while installing, installation stopped.\033[0m" && rm -r -f "v$INSTALL_VERSION" && rm -r -f "$APP_NAME" && rm -r -f "__MACOSX" && rm -r -f "payload" && exit 1;